    private void btnIMCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIMCActionPerformed
        
        Double pesoDigitado = Double.valueOf(txtPeso.getText());
        Double alturaDigitada = Double.valueOf(txtAltura.getText());
        
        Double total = pesoDigitado / (alturaDigitada * alturaDigitada);
       
        
        lblIMC.setText(String.format("O seu IMC Ã© de %.2f", total));
        
    }//GEN-LAST:event_btnIMCActionPerformed

